import { Link } from "react-router-dom";
import './Card.css';


const Card = ({id,name,status,species,gender,origin,image,onClose}) => {
   return (
      <id>
         <section>
            <button onClick={() => onClose(id)}>X</button>
            <div style={{ position: "relative" }}>
               <h3 className="card-name" style={{ marginBottom: "10px" }}>{name}</h3>
               <Link to={`/detail/${id}`} style={{ display: "block" }}>
                  <img src={image} alt={name} />
               </Link>
            </div>

            <div className="single-card" font->
               <h2>{name}</h2>
               <h2>{status}</h2>
               <h2>{species}</h2>
               <h2>{gender}</h2>
               <h2>{origin}</h2>
            </div>
         </section>
      </id>
   );
}

export default Card;
