import { createRoot } from "react-dom/client";

const Form = () => {
  const [userData, setUserData] = useState({
    email: '',
    password: ''
  })

  const [errors, setErrors] = useState({
    email: '',
    password: ''
  })

  const handleOnChange = (event) => {
    setUserData((prevState) => ({
      ...prevState,
      [event.target.name]: event.target.value
    }))
  }

  const validate = () => {
    if (!/^[\w.%+-]+@[\w.-]+\.[A-Za-z]{2,}$/.test(userData.email)) {
      setErrors({
        ...errors,
        email: 'email inválido'
      })
    } else {
      setErrors({
        ...errors,
        email: ''
      })
    }

    if (userData.password.length < 6) {
      setErrors({
        ...errors,
        password: "mas de 6 caracteres por favor"
      })
    } else {
      setErrors({
        ...errors,
        password: ''
      })
    }
  }

  const handleOnSubmit = (event) => {
    event.preventDefault()
  }

  return (
    <form onSubmit={handleOnSubmit}>
      <h1>Holi, soy el form</h1>
      <label htmlFor="email">Email</label>
      <input
        name="email"
        type="email"
        placeholder="Ingrese su email"
        value={userData.email}
        onChange={handleOnChange}
        onBlur={validate}
      />
      {errors.email && <p>{errors.email}</p>}
      <hr />
      <label htmlFor="password">Password</label>
      <input
        name="password"
        type="text"
        placeholder="Ingrese una password"
        value={userData.password}
        onChange={handleOnChange}
        onBlur={validate}
      />
      {errors.password && <p>{errors.password}</p>}
      <hr />
      <button disabled={!userData.email || !userData.password || errors.email || errors.password} >Enviar</button>
    </form>
  )
}

export default Form
