export const validate = (userData) => {
    let errors = {
      email: '',
      password: ''
    };
    
    if (!/\S+@\S+\.\S+/.test(userData.email) && !userData.email === '' && !userData.length <=35) {
      errors.email = 'por favor, revisa tu email, rey/reina';
    }
    
    if (!/^(?=.*[0-9])[a-zA-Z0-9]{6,10}$/) {
      errors.password = 'tiene que tener un mínimo de 6 caracteres';
    }
    
    return errors;
  }
