import { useState } from 'react';
import './Cards.css';
import Card from '../Card/Card';

const Cards = ({ characters, onClose }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [translateX, setTranslateX] = useState(0);
  const cardWidth = 280; // Ancho de cada carta en píxeles
  const numVisibleCards = 5; // Número de cartas visibles en el carrousel
  const totalCards = characters.length + 1; // Añade una carta vacía
  const maxIndex = totalCards - numVisibleCards;
  const nextDisabled = currentIndex >= maxIndex;
  const prevDisabled = currentIndex === 0;

  const handleNextClick = () => {
    if (nextDisabled) {
      return;
    }

    const newTranslateX = translateX - cardWidth;
    setTranslateX(newTranslateX);
    setCurrentIndex(currentIndex + 1);
  };

  const handlePrevClick = () => {
    if (prevDisabled) {
      return;
    }

    const newTranslateX = translateX + cardWidth;
    setTranslateX(newTranslateX);
    setCurrentIndex(currentIndex - 1);
  };

  // Agrega la carta vacía al principio de la matriz de personajes
  const charactersWithEmptyCard = [{ empty: true }, ...characters];

  // Determina si la carta vacía debe ser visible o no
  const hideEmptyCard = true;

  let wrapperStyle = { transform: `translateX(${translateX}px)` };
  if (charactersWithEmptyCard.length === 1) {
    // Si solo hay una carta, centrarla en la pantalla
    wrapperStyle = {
      ...wrapperStyle,
      display: 'flex',  
      justifyContent: 'left',
      alignItems: 'center'
    };
  }

  return (
    <section className="cards-container">
      <div className="carousel-controls">
        <button className="carousel-control" onClick={handlePrevClick} disabled={prevDisabled}>
          Prev
        </button>
        <button className="carousel-control" onClick={handleNextClick} disabled={nextDisabled}>
          Next
        </button>
      </div>
      <div className="cards-wrapper" style={wrapperStyle}>
        {charactersWithEmptyCard.map((character, index) => {
          if (character.empty && hideEmptyCard) {
            return null;
          }
          return (
            <Card
              key={character.id || index} // Usa el índice como clave para la carta vacía
              id={character.id}
              name={character.name}
              status={character.status}
              species={character.species}
              gender={character.gender}
              origin={character.origin?.name}
              image={character.image}
              onClose={onClose}
              className={character.empty ? 'card-empty' : ''}
            />
          )
        })}
      </div>
    </section>
  );
};

export default Cards;
