        validate()
    }

    const validate = () => {
        if(!/\S+@\S+\.\S+/.test(form.email)){
            setErrors({
                ...errors,
                email: 'por favor, revisa tu email, rey/reina'
            })
        }
        else setErrors({...errors, email: ''})

        if(form.password.length < 6){
            setErrors({
                ...errors,
                password: 'tiene que tener un mínimo de 6 caracteres'
            })
        }
        else setErrors({...errors, password: ''})
    }

    const handleOnSubmit = (event) => {
        event.preventDefault()
    }

    return(
        <form onSubmit={handleOnSubmit}>
            <h1>Holi, soy el Form</h1>

            <label htmlFor="email">Email: </label>
            <input name="email" type="email" placeholder="ingrese su mail" value={form.email} onChange={handleOnChange} />
            {errors.email && <p>{errors.email}</p>}
            <hr />
            <label htmlFor="password">Password: </label>
            <input name="password" type="text" placeholder="ingrese una password" value={form.password} onChange={handleOnChange}  />
            {errors.password && <p>{errors.password}</p>}
            <hr />
            <button disabled={!form.email || !form.password || errors.email || errors.password} >Enviar</button>
        </form>
    )
}

export default Form;
