import { useState } from 'react';
import './Cards.css';
import Card from '../Card/Card';

const Cards = ({ characters, onClose }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const cardWidth = 280; // Ancho de cada carta en píxeles
  const numVisibleCards = 4; // Número de cartas visibles en el carrousel

  const handleNextClick = () => {
    if (currentIndex < characters.length - numVisibleCards) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handlePrevClick = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  return (
    <section className="cards-container">
      <div className="cards-wrapper" style={{ transform: `translateX(-${currentIndex * cardWidth}px)` }}>
        {characters.map((character) => (
          <Card
            key={character.id}
            id={character.id}
            name={character.name}
            status={character.status}
            species={character.species}
            gender={character.gender}
            origin={character.origin.name}
            image={character.image}
            onClose={onClose}
          />
        ))}
      </div>
      <div className="carousel-controls">
        <button className="carousel-control" onClick={handlePrevClick}>
          Prev
        </button>
        <button className="carousel-control" onClick={handleNextClick}>
          Next
        </button>
      </div>
    </section>
  );
};

export default Cards;
