import './App.css';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav';
import { useState } from 'react';
import axios from 'axios'
import {useLocation,Routes,Route} from 'react-router-dom'
import Detail from './components/Detail';
import About from "./components/About"
import Error from './components/Error';
import Form from './components/Form';

//const URL_BASE = 'https://be-a-rym.up.railway.app/api/character'
//const API_KEY ='921c53ed19ee.c07a3c34e20b05d4765f'

function App() {
   const [characters, setCharacters] = useState([]); //destructurin con array porque el useState devuelve un array
   
   const onSearch =(id) => {
      axios(`https://rickandmortyapi.com/api/character/${id}`) //
      //axios(`${URL_BASE}/${id}?key=${API_KEY}`)
      .then(response => response.data)
      .then((data) => {
         if (data.id) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            alert('¡No hay personajes con este ID!');
         }
      });
   }

   const onClose =(id) => {
      const charactersfiltered = characters.filter(character => character.id !== Number(id))
      setCharacters(charactersfiltered)
   }
   
   const location = useLocation(); //lo utilizo para saber si estoy sobre el login para no mostrar la NAV
      
   return (
      <div className='App'>
         {location.pathname !== "/" && ( //si estoy en el path 
            <Nav onSearch={onSearch}/>
         )}

         <Routes>
               <Route path='/' element={<Form />} />
               <Route path='/Home' element={<Cards characters={characters} onClose={onClose}/>} />
               <Route path='/About' element={<About/>} />
               <Route path='/Detail/:id' element={<Detail/>} />
               <Route path="*" element={<Error />} />
               
         </Routes>

          
      </div>
   );
}

export default App;
