import {ADD_FAV, REMOVE_FAV, FILTER,ORDER} from './types'


const initialState = {
    myFavorites: [],
    allCharacter: [],
    
} 

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_FAV:
          return { 
            ...state, 
            myFavorites: [...state.allCharacter,action.payload], 
            allCharacter: [...state.allCharacter,action.payload]
          };
        case REMOVE_FAV:
          return { 
            ...state, 
            myFavorites: state.myFavorites.filter(fav => fav.id !== action.payload) 
          };
        case FILTER:
            const allCharacterFiltered = state.allCharacter.filter(character => character.gender === action.payload);
            return {
              ...state,
              myFavorites: action.payload === 'All' ? [...state.allCharacter] : [...allCharacterFiltered],
            };

        case ORDER:
                const allCharacterFavCopy = [...state.allCharacterFav]; // Usar spread operator para copiar el array
                return {
                  ...state,
                  myFavorites:
                    action.payload === 'A'
                      ? allCharacterFavCopy.sort((a, b) => a.id - b.id)
                      : allCharacterFavCopy.sort((a, b) => b.id - a.id)
                };
              
        default:
            return {
                ...state
            }
    }
}

export default reducer;
