import { useState } from 'react';
import './Cards.css';
import Card from '../Card/Card';

const Cards = ({ characters, onClose }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [translateX, setTranslateX] = useState(-cardWidth);
  const cardWidth = 280; // Ancho de cada carta en píxeles
  const numVisibleCards = 3; // Número de cartas visibles en el carrousel
  const totalCards = characters.length;
  const maxIndex = totalCards - numVisibleCards;
  const nextDisabled = currentIndex >= maxIndex;
  const prevDisabled = currentIndex === 0;

  const handleNextClick = () => {
    if (nextDisabled) {
      return;
    }

    const newTranslateX = translateX - cardWidth;
    setTranslateX(newTranslateX);
    setCurrentIndex(currentIndex + 1);
  };

  const handlePrevClick = () => {
    if (prevDisabled) {
      return;
    }

    const newTranslateX = translateX + cardWidth;
    setTranslateX(newTranslateX);
    setCurrentIndex(currentIndex - 1);
  };

  const visibleCharacters = characters.slice(currentIndex, currentIndex + numVisibleCards);

  return (
    <section className="cards-container">
      <div className="carousel-controls">
        <button className="carousel-control" onClick={handlePrevClick} disabled={prevDisabled}>
          Prev
        </button>
        <button className="carousel-control" onClick={handleNextClick} disabled={nextDisabled}>
          Next
        </button>
      </div>
      <div className="cards-wrapper" style={{ transform: `translateX(${translateX}px)` }}>
        {visibleCharacters.map((character) => (
          <Card
            key={character.id}
            id={character.id}
            name={character.name}
            status={character.status}
            species={character.species}
            gender={character.gender}
            origin={character.origin.name}
            image={character.image}
            onClose={onClose}
          />
        ))}
      </div>
    </section>
  );
};

export default Cards;
