import { useState } from "react";

const Form = () => {
    const [userData, setUserData] = useState({
        email: '',
        password: ''
    })

    const [errors, setErrors] = useState({
        email: '',
        password: ''
    })

    const handleOnChange = (event) => {
        console.log(event.target.name);
        setUserData({
            ...userData,
            [event.target.name]: event.target.value
        })
        validate()
    }

    const validate = () => {
        if (!/\S+@\S+\.\S+/.test(userData.email)) {
          setErrors((prevState) => ({
            ...prevState,
            email: 'por favor, revisa tu email, rey/reina'
          }));
        } else {
          setErrors((prevState) => ({
            ...prevState,
            email: ''
          }));
        }
    
        if (userData.password.length < 6) {
          setErrors((prevState) => ({
            ...prevState,
            password: 'tiene que tener un mínimo de 6 caracteres'
          }));
        } else {
          setErrors((prevState) => ({
            ...prevState,
            password: ''
          }));
        }
    };
    
    

    const handleOnSubmit = (event) => {
        event.preventDefault()
    }

    return(
        <form onSubmit={handleOnSubmit}>
            <h1>Holi, soy el Form</h1>

            <label htmlFor="email">Email: </label>
            <input name="email" type="email" placeholder="ingrese su mail" value={userData.email} onChange={handleOnChange} />
            {errors.email && <p>{errors.email}</p>}
            <hr />
            <label htmlFor="password">Password: </label>
            <input name="password" type="password" placeholder="ingrese una password" value={userData.password} onChange={handleOnChange}  />
            {errors.password && <p>{errors.password}</p>}
            <hr />
            <button disabled={!userData.email || !userData.password || errors.email || errors.password} >Enviar</button>
        </form>
    )
}

export default Form;
