import axios from "axios"
import { useParams } from "react-router-dom"
import { useEffect, useState } from "react";

const Detail = () => {
    const {id} = useParams();
    const [character, setCharacter] = useState({})
    
    useEffect(() => {
        axios(`https://rickandmortyapi.com/api/character/${id}`)
        .then(({ data }) => {
           if (data.name) {
              setCharacter(data);
           } else {
              window.alert('No hay personajes con ese ID');
           }
        });
        return setCharacter({});
     }, [id]);

    return (
        <div>
            {character.name ? <p>Name: {character.name}</p> : null}
            {character.status ? <p>Status: {character.status}</p> : null}
    {character.species ? <p>Species: {character.species}</p> : null}
    {character.gender ? <p>Gender: {character.gender}</p> : null}
    {character.origin ? <p>Origin: {character.origin.name}</p> : null}
    {character.image ? <img src={character.image} alt={character.name} /> : null}
        </div>
    )
    
}

export default Detail
