import {ADD_FAV, REMOVE_FAV} from './types'

const initialState = {
    myFavorites: []
}

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_FAV:
            return {
                ...state,
                myFavorites: [...state.myFavoritesmyFavorites, action.payload]
            }
        case REMOVE_FAV:
            return {
                ...state,
                myFavorites: state.myFavorites.filter(
                    (fav) => fav.id !== parseInt(action.payload)
                  )
            }
        default:
            return {
                ...state
            }
    }
}

export default reducer;
