import {ADD_FAV, REMOVE_FAV, FILTER,ORDER} from './types'


const initialState = {
    myFavorites: [],
    all: [],
    
} 

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_FAV:
          return { 
            ...state, 
            myFavorites: [...state.all,action.payload], 
            all: [...state.all,action.payload]
          };
        case REMOVE_FAV:
          return { 
            ...state, 
            myFavorites: state.myFavorites.filter(fav => fav.id !== action.payload) 
          };
        case FILTER:
            const allFiltered = state.all.filter(character => character.gender === action.payload);
            return {
              ...state,
              myFavorites: action.payload === 'All' ? [...state.all] : [...allFiltered],
            };

        case ORDER:
                const allFavCopy = [...state.allFav]; // Usar spread operator para copiar el array
                return {
                  ...state,
                  myFavorites:
                    action.payload === 'A'
                      ? allFavCopy.sort((a, b) => a.id - b.id)
                      : allFavCopy.sort((a, b) => b.id - a.id)
                };
              
        default:
            return {
                ...state
            }
    }
}

export default reducer;
