import { useState } from "react";
import { validate } from './validation';

const Form = () => {
    const [userData, setUserData] = useState({
        email: '',
        password: ''
    })

    const [errors, setErrors] = useState({
        email: '',
        password: ''
    })

    const [validFields, setValidFields] = useState({
        email: false,
        password: false
    })

    const handleOnChange = (event) => {
        setUserData({
            ...userData,
            [event.target.name]: event.target.value
        })
        
        // Invocar la función de validación
        const errors = validate(userData);
        setErrors(errors);

        // Verificar si los campos son válidos
        setValidFields({
            email: errors.email === '',
            password: errors.password === ''
        });
    }

    const handleOnSubmit = (event) => {
        event.preventDefault()
    }

    return(
        <form onSubmit={handleOnSubmit}>
            <h1>Holi, soy el Form</h1>

            <label htmlFor="email">Email: </label>
            <div style={{ display: "flex" }}>
                <input name="email" type="email" placeholder="ingrese su mail" value={userData.email} onChange={handleOnChange} />
                {validFields.email && <i className="fa fa-check-circle" style={{ color: "green", marginLeft: "10px" }}></i>}
                {errors.email && !validFields.email && <i className="fa fa-times-circle" style={{ color: "red", marginLeft: "10px" }}></i>}
            </div>
            <hr />
            <label htmlFor="password">Password: </label>
            <div style={{ display: "flex" }}>
                <input name="password" type="text" placeholder="ingrese una password" value={userData.password} onChange={handleOnChange}  />
                {validFields.password && <i className="fa fa-check-circle" style={{ color: "green", marginLeft: "10px" }}></i>}
                {errors.password && !validFields.password && <i className="fa fa-times-circle" style={{ color: "red", marginLeft: "10px" }}></i>}
            </div>
            <hr />
            <button disabled={!userData.email || !userData.password || errors.email || errors.password} >Enviar</button>
        </form>
    )
}

export default Form;



















