import {ADD_FAV, REMOVE_FAV, FILTER,ORDER} from './types'

const initialState = {
  myFavorites: [],
  allCharacters: []
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAV:
      return {
        ...state,
        myFavorites: [...state.myFavorites, action.payload]
      };
    case REMOVE_FAV:
      const updatedFavorites = state.myFavorites.filter(
        character => character.id !== action.payload.id
      );
      return {
        ...state,
        myFavorites: updatedFavorites
      };
    case FILTER:
      const allCharactersFiltered = state.allCharacters.filter(
        character => character.gender === action.payload
      );
      const myFavoritesFiltered = state.myFavorites.filter(
        character => character.gender === action.payload
      );
      return {
        ...state,
        myFavorites:
          action.payload === 'All'
            ? [...state.allCharacters]
            : [...myFavoritesFiltered],
      };
    case ORDER:
      const allCharactersFavCopy = [...state.myFavorites];
      return {
        ...state,
        myFavorites:
          action.payload === 'A'
            ? allCharactersFavCopy.sort((a, b) => a.id - b.id)
            : allCharactersFavCopy.sort((a, b) => b.id - a.id)
      };
    default:
      return {
        ...state
      };
  }
};

export default reducer;
